mapper.select:
SELECT ticket_id, passenger_id, flight_id, seat_number, price
FROM Tickets
WHERE passenger_id = #{passengerId}

mapper.update:
UPDATE Tickets
SET passenger_id = #{passengerId},
    flight_id = #{flightId},
    seat_number = #{seatNumber},
    price = #{price}
WHERE ticket_id = #{ticketId}
